      <h3>{room}</h3>
      <div className="appName">
        <h2>
          Chatvy
        </h2>
          <span role="img" aria-label="emoji">
            ðŸ’¬
          </span>
        
      </div>
      {/* <h1> Chatvy</h1> */}
    </div>
    <div className="rightInnerContainer">
