import React, { useState } from "react";
import M from "materialize-css";
import { Link, useHistory } from "react-router-dom";

function Signup() {
  const [username, setuserName] = useState("");
  //   const [lastName, setlastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setconfirmPassword] = useState("");

  const postData = () => {
    if (
      !/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(
        email
      )
    ) {
      M.toast({ html: "invalid Email!", classes: "#d32f2f red darken-2" });
      return;
    }

    fetch("http://localhost:5000/signup", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username,
        email,
        password,
      }),
    })
      .then((res) => {
        console.log(username, email, password);
        return res.json();
      })
      .then((data) => {
        if (data.error) {
          M.toast({
            html: "User Already Exit!",
            classes: "#d32f2f red darken-2",
          });
        } else
          M.toast({
            html: "Succesfully sent activation link!",
            classes: "#00c853 green accent-4",
          });
        history.push("/login");
        console.log(data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    e.stopPropagation();
    const obj = {
      email: email,
      password: password,
    };
    console.log(obj);
    setuserName("");
    // setlastName("");
    setEmail("");
    setPassword("");
    setconfirmPassword("");
  };
  return (
    <div className="signup-page">
      {/* <h1> Login </h1> */}
      <form onSubmit={handleSubmit}>
        <h1> Sign up </h1>
        <div class="form-group">
          <label for="exampleInputName1">User Name</label>
          <input
            type="User Name"
            class="form-control"
            id="exampleInputName1"
            placeholder="Enter your Name"
            value={username}
            onChange={(e) => setuserName(e.target.value)}
            required
          />
        </div>
        <div class="form-group">
          <label for="exampleInputEmail1">Email address</label>
          <input
            type="email"
            class="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
            placeholder="Enter your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          {/* <small id="emailHelp" class="form-text text-muted">
            We'll never share your email with anyone else.
          </small> */}
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1">Password</label>
          <input
            type="password"
            class="form-control"
            id="exampleInputPassword1"
            placeholder="Enter your Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1">Confirm Password</label>
          <input
            type="password"
            class="form-control"
            id="exampleInputPassword1"
            placeholder="Enter your Password"
            value={confirmPassword}
            onChange={(e) => setconfirmPassword(e.target.value)}
          />
        </div>
        <div class="form-group form-check">
          <input type="checkbox" class="form-check-input" id="exampleCheck1" />
          <label class="form-check-label" for="exampleCheck1">
            I accept the Terms of use and Privacy
          </label>
        </div>
        <button type="Sign Up" class="btn btn-primary" onClick={postData}>
          Sign Up
        </button>
      </form>
    </div>
  );
}

export default Signup;
